"""
create code based on category and fork version
"""

EVM_CATEGORY = namedtuple("EVM_CATEGORY", ["min", "max", "avg",
                                                       "distribution", "distribution_prolog", "distribution_epilog"])
EVM_CATEGORY.min = 1
EVM_CATEGORY.max = 10000
EVM_CATEGORY.avg = 800

EVM_CATEGORY.distribution_prolog = {}
EVM_CATEGORY.distribution = {}
EVM_CATEGORY.distribution_epilog = {}

for cat, instructions in evmdasm.registry.registry.by_category.items():
    likelyhood_prolog, likelyhood, likelyhood_epilog = 0.30, 0.30, 0.30  # default likelyhood
    if cat=="terminate":
        # we do not want it to likely terminate
        likelyhood = 0.01
        likelyhood_prolog = 0.001
        likelyhood_epilog = 0.5
    elif cat=="arithmetic":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.5, 0.8, 0.5
    elif cat=="comparison":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.05, 0.05, 0.05
    elif cat=="bitwise-logic":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.6, 0.6, 0.6
    elif cat=="cryptographic":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.01, 0.1, 0.01
    elif cat=="envinfo":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.6, 0.4, 0.01
    elif cat=="blockinfo":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.6, 0.4, 0.01
    elif cat=="stack":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.6, 0.25, 0.2  # we add pushes anyway
    elif cat=="memory":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.3, 0.3, 0.3
    elif cat=="storage":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.4, 0.4, 0.4
    elif cat == "controlflow":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.2, 0.6, 0.2
    elif cat=="label":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.3, 0.3, 0.3
    elif cat=="event":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.01, 0.1, 0.01
    elif cat=="system":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.2, 0.5, 0.4
    elif cat=="unofficial":
        likelyhood, likelyhood_epilog, likelyhood_prolog = 0.1, 0.1, 0.1

    for i in instructions:
        if i.fork=="constantinople":  # prio on constantinople
            likelyhood = 0.7
        elif i.name.startswith("PUSH"):
            likelyhood = 0.001  # reduce likelyhood of additional pushes
            likelyhood_epilog = 0.001  # reduce likelyhood of additional pushes
        EVM_CATEGORY.distribution_prolog[i.opcode] = likelyhood_prolog
        EVM_CATEGORY.distribution[i.opcode] = likelyhood
        EVM_CATEGORY.distribution_epilog[i.opcode] = likelyhood_epilog
